(defn generate-file-mother [number-of-files]
  (let [required-ns (str/join "\n"
                           (for [n (range 1 (inc number-of-files))]
                             (str "[flappy-bird-demo.generated.test-" n " :as test-" n "]")))
        call-fn-str (str/join "\n"
                              (for [n (range 1 (inc number-of-files))]
                                (str "(test-" n "/text)")))]
    (spit "src/flappy_bird_demo/generated/mother.cljs"
          (str "(ns flappy-bird-demo.generated.mother"
               (when (not-empty required-ns) (str "\n\t(:require " required-ns ")")) ")"
               (when (not-empty call-fn-str) (str "\n\n" call-fn-str))))))

(defn generate-files [number-of-files]
  (prepare-directory)
  (doseq [n (range 1 (inc number-of-files))]
    (generate-file n))
  (generate-file-mother number-of-files))

(defn -main [& args]
  (if-let [number-of-files (parse-number (first args))]
    (do (when (> number-of-files 10000)
          (prn "WARNING: Loading all these files in the browser will take a lot of time!"))
        (prn (str "Starting to generate " number-of-files " files"))
        (generate-files number-of-files)
        (prn "Finished generating files"))
    (prn (str "Invalid input. Needs a valid integer. Was: " (first args)))))